import React, { Component } from 'react';
import Column from "./Column";

const MOVE_LEFT = -1;
const MOVE_RIGTH = 1;

class App extends Component {

  constructor(props){
      super(props);
      this.state = {
          columns: [
          {
              name: "Line 1",
              cards: [{name: "Kanban Card A"}]
          },
           {
              name: "Line 2",
              cards: [{name: "Kanban A"}]
          },

           {
              name: "Line 3",
              cards: []
          }
          ],
        textInput: ''

     }
     this.handleAddCard = this.handleAddCard.bind(this);
     this.onChangeText = this.onChangeText.bind(this);


  }


  onChangeText = e => {
      this.setState({textInput: e.target.value})
  }


  handleAddCard = () => {
      let newCardValue = {name: this.state.textInput}
      this.setState(prevState => {
          const {columns} = prevState
          columns[0].cards.push(newCardValue);
          return {columns}
      })
 }

 handleMoveCard = (columnIndex, cardIndex, moveDirection) => {
     this.setState(prevState => {
         const {columns} = prevState;
         const [card] = columns[columnIndex].cards.splice(cardIndex, 1)
         columns[columnIndex + moveDirection].cards.push(card)
         return {columns}
     })
 }




  render() {
    return (

       <div className="header">
       <input className="card-input"
            placeholder="My Name Card"
            value={this.state.textInput}
            onChange={this.onChangeText} 
        />
        <span className="card-btn" onClick={this.handleAddCard}>Add Card</span>
        <div className="board">
            {this.state.columns.map((column, columnIndex) => (
                <Column 
                    column = {column}
                    columnIndex = {columnIndex}
                    onMoveLeft = {cardIndex => this.handleMoveCard(columnIndex, cardIndex, MOVE_LEFT)}
                    onMoveRight = {cardIndex => this.handleMoveCard(columnIndex, cardIndex, MOVE_RIGHT)}
                />
            ))}
        </div>
        </div>

    )}
}

export default App;
